DROP TABLE IF EXISTS `_store_yd_chapt`;
CREATE TABLE IF NOT EXISTS `_store_yd_chapt` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章ID',
  `article_type` int(2) NOT NULL DEFAULT '0' COMMENT '0:小物件老文章，1:新展示类型文章，2:新产品',
  `renewtime` varchar(30) NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '自动更新时间',
  `order_id` int(5) NOT NULL COMMENT '排序',
  `user_id` int(11) NOT NULL COMMENT '用户id',
  `cover_type` tinyint(1) NOT NULL COMMENT '封面类型 0（不显示或不存在）1（图片） 2 （视频）',
  `cover_url` varchar(255) NOT NULL COMMENT '视频地址',
  `cover_describe` varchar(255) NOT NULL COMMENT '封面描述',
  `is_cover_story` int(2) NOT NULL DEFAULT '0' COMMENT '是否为改期封面故事',
  `maga_id` int(11) NOT NULL COMMENT '杂志ID',
  `issue_id` int(11) NOT NULL DEFAULT '0' COMMENT '期刊id',
  `if_recommend` tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT '是否推荐，0:不推荐 (现修改成小图推荐)，1:推荐',
  `maga_all_number` int(11) NOT NULL COMMENT '杂志的总期号',
  `category_level1_id` int(11) NOT NULL DEFAULT '0' COMMENT '第一级分类的id',
  `category_level2_id` int(11) NOT NULL DEFAULT '0' COMMENT '第二级分类的id',
  `pid` int(11) NOT NULL COMMENT '图片ID',
  `if_h_pic` tinyint(2) NOT NULL DEFAULT '0' COMMENT '是否有横片的题图',
  `cover_pic_type` tinyint(2) NOT NULL DEFAULT '0' COMMENT '首页图，0,方形,1,横片',
  `horizon_pic_id` int(11) NOT NULL DEFAULT '0' COMMENT '横图的id',
  `ipad_id` int(11) NOT NULL COMMENT 'ipad中此文章对应的ID',
  `chapt_title` varchar(250) NOT NULL COMMENT '文章标题',
  `index_title` varchar(250) DEFAULT NULL COMMENT '首页标题',
  `chapt_brief` varchar(256) NOT NULL COMMENT '文章简介',
  `chapt_content` mediumtext NOT NULL COMMENT '文章内容',
  `chapt_url` varchar(200) DEFAULT NULL COMMENT '文章外链',
  `chapt_content_back` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL COMMENT '整理后文章内容',
  `pic_author` varchar(16) NOT NULL COMMENT '图片作者',
  `chapt_reporter` varchar(200) NOT NULL DEFAULT '' COMMENT '文章记者',
  `chapt_editor` varchar(20) NOT NULL COMMENT '文章编辑',
  `chapt_editor_mail` varchar(64) NOT NULL COMMENT '编辑邮箱地址',
  `chapt_time` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '更新时间',
  `access_level` tinyint(2) NOT NULL COMMENT '访问权限',
  `click_count` int(11) NOT NULL COMMENT '阅读次数',
  `favour_count` int(11) NOT NULL COMMENT '收藏次数',
  `comm_count` int(11) NOT NULL COMMENT '评论总数',
  `seek_count` int(11) NOT NULL DEFAULT '0' COMMENT '被搜索的次数',
  `like_count` int(11) NOT NULL DEFAULT '0' COMMENT '喜欢的统计',
  `reship_count` int(11) NOT NULL COMMENT '转发数',
  `if_comment` tinyint(4) NOT NULL COMMENT '是否允许评论',
  `if_reship` tinyint(4) NOT NULL COMMENT '是否允许转载',
  `state` char(8) NOT NULL COMMENT '发布状态 0 未发布 1 发布',
  `type` char(4) NOT NULL COMMENT '文章类型：pt:普通|ts：图说',
  `if_new` tinyint(1) NOT NULL DEFAULT '1' COMMENT '0 导过来的旧数据 1 新后台添加的新数据',
  `orig_zk_id` int(11) NOT NULL DEFAULT '0' COMMENT '原始的周刊id',
  `orig_hy_id` int(11) NOT NULL DEFAULT '0' COMMENT '原始的好运id',
  `if_big_recommend` tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT '杂志目录大图推荐 0：不推荐',
  `cover_url_say` varchar(200) NOT NULL COMMENT '封面图说',
  PRIMARY KEY (`id`),
  KEY `ipadid` (`ipad_id`),
  KEY `issue_id` (`issue_id`,`state`),
  KEY `cover_type` (`cover_type`),
  KEY `category_level2_id` (`category_level2_id`),
  KEY `click_count` (`click_count`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='store阅读文章表' AUTO_INCREMENT=21742 ;

